var Login = function () {
    var e = function () {
        $(".login-form").validate({
            errorElement: "span",
            errorClass: "help-block",
            focusInvalid: !1,
            rules: {username: {required: !0}, password: {required: !0}, remember: {required: !1}},
            messages: {username: {required: "邮箱/手机号必填。"}, password: {required: "密码必填。"}},
            invalidHandler: function (e, r) {
                $(".alert-danger", $(".login-form")).show()
            },
            highlight: function (e) {
                $(e).closest(".form-group").addClass("has-error")
            },
            success: function (e) {
                e.closest(".form-group").removeClass("has-error"), e.remove()
            },
            errorPlacement: function (e, r) {
                e.insertAfter(r.closest(".input-icon"))
            },
            submitHandler: function (e) {
                // alert("submit!")
                // 选取id= username的元素  class 使用.   jquery根据name属性查找 input[name='username']
                var email = $.trim($("input[name='username']").val());
                var password = $.trim($("input[name='password']").val());
                password = md5(password);
                $(":submit").prop('disabled', true);
                $.ajax({
                    url:'/login',
                    type:'post',
                    dataType:'json',
                    data:{
                        "email":email,
                        "password":password
                    },
                    success:function (json) {
                        if (json.ret == "0"){
                            location.href = "/home";
                        }else{
                            $alertError.text(json.msg);
						    $alertError.show();
                        }
                    },
                    error : function(XMLHttpRequest, textStatus, errorThrown) {
                        $alertError.text("发生未知错误，请联系管理员。")
                        $alertError.show();
				    },
				    complete: function() {
                    //    $(":button")	选取所有 type="button" 的 <input> 元素 和 <button> 元素
                    $(":submit").prop('disabled', false);
                    // prop() 方法设置或返回被选元素的属性和值。
// 当该方法用于返回属性值时，则返回第一个匹配元素的值。
// 当该方法用于设置属性值时，则为匹配元素集合设置一个或多个属性/值对。
				    }
                });
            }
        }), $(".login-form input").keypress(function (e) {
            if (13 == e.which) return $(".login-form").validate().form() && $(".login-form").submit(), !1
        })
    }, r = function () {
        $(".forget-form").validate({
            errorElement: "span",
            errorClass: "help-block",
            focusInvalid: !1,
            ignore: "",
            rules: {email: {required: !0, email: !0}},
            messages: {email: {required: "Email is required."}},
            invalidHandler: function (e, r) {
            },
            highlight: function (e) {
                $(e).closest(".form-group").addClass("has-error")
            },
            success: function (e) {
                e.closest(".form-group").removeClass("has-error"), e.remove()
            },
            errorPlacement: function (e, r) {
                e.insertAfter(r.closest(".input-icon"))
            },
            submitHandler: function (e) {
                e.submit()
            }
        }), $(".forget-form input").keypress(function (e) {
            if (13 == e.which) return $(".forget-form").validate().form() && $(".forget-form").submit(), !1
        }), jQuery("#forget-password").click(function () {
            jQuery(".login-form").hide(), jQuery(".forget-form").show()
        }), jQuery("#back-btn").click(function () {
            jQuery(".login-form").show(), jQuery(".forget-form").hide()
        })
    }, i = function () {
        function e(e) {
            if (!e.id) return e.text;
            var r = $('<span><img src="../assets/global/img/flags/' + e.element.value.toLowerCase() + '.png" class="img-flag" /> ' + e.text + "</span>");
            return r
        }

        jQuery().select2 && $("#country_list").size() > 0 && ($("#country_list").select2({
            placeholder: '<i class="fa fa-map-marker"></i>&nbsp;选择国籍',
            templateResult: e,
            templateSelection: e,
            width: "auto",
            escapeMarkup: function (e) {
                return e
            }
        }), $("#country_list").change(function () {
            $(".register-form").validate().element($(this))
        })), $(".register-form").validate({
            errorElement: "span",
            errorClass: "help-block",
            focusInvalid: !1,
            ignore: "",
            rules: {
                fullname: {required: !0},
                email: {required: !0, email: !0},
                address: {required: !0},
                city: {required: !0},
                country: {required: !0},
                username: {required: !0},
                password: {required: !0},
                rpassword: {equalTo: "#register_password"},
                tnc: {required: !0}
            },
            messages: {tnc: {required: "Please accept TNC first."}},
            invalidHandler: function (e, r) {
            },
            highlight: function (e) {
                $(e).closest(".form-group").addClass("has-error")
            },
            success: function (e) {
                e.closest(".form-group").removeClass("has-error"), e.remove()
            },
            errorPlacement: function (e, r) {
                "tnc" == r.attr("name") ? e.insertAfter($("#register_tnc_error")) : 1 === r.closest(".input-icon").size() ? e.insertAfter(r.closest(".input-icon")) : e.insertAfter(r)
            },
            submitHandler: function (e) {
                alert("register!");
                e[0].submit()
            }
        }), $(".register-form input").keypress(function (e) {
            if (13 == e.which) return $(".register-form").validate().form() && $(".register-form").submit(), !1
        }), jQuery("#register-btn").click(function () {
            jQuery(".login-form").hide(), jQuery(".register-form").show()
        }), jQuery("#register-back-btn").click(function () {
            jQuery(".login-form").show(), jQuery(".register-form").hide()
        })
    };
        /*
        login = function(){
        var username = $.trim($("#username").val());
        var password = $.trim()
    };
    */
    return {
        init: function () {
            e(), r(), i()
        }
    }
}();

jQuery(document).ready(function () {
    Login.init()
});